/**
 * Format time
 * @method format
 * @param  {number} sec
 * @param  {string} separator
 * @return {string} HTML
 */
function format(sec, separator) {
  var parsed = parse(sec);
  var result = [];

  if (typeof separator !== 'string') {
    separator = ':'
  }

  if (parsed.hour > 0) {
    result.push('<span class="hour">' + parsed.hour + '</span>');
  }

  result.push('<span class="min">' + parsed.min + '</span>');
  result.push('<span class="sec">' + parsed.sec + '</span>');

  return result.join('<span class="separator">' + separator + '</span>');
}

/**
 * Parse time
 * @method parse
 * @param  {number} sec Seconds
 * @return {object}
 */
function parse(sec) {
  var h, m, s;

  sec = Math.floor(sec);

  if (sec >= 0) {
    h = Math.floor(sec / 3600);
    m = Math.floor((sec - h * 3600) / 60);
    s = sec - h * 3600 - m * 60;
    if (h < 10) {
      h = '0' + h;
    }
    if (s < 10) {
      s = '0' + s;
    }
    if (m < 10) {
      m = '0' + m;
    }
  } else {
    sec = Math.abs(sec);
    h = Math.floor(sec / 3600);
    m = Math.floor((sec - h * 3600) / 60);
    s = sec - h * 3600 - m * 60;
    if (h < 10) {
      h = '0' + h;
    }
    if (s < 10) {
      s = '0' + s;
    }
    if (m < 10) {
      m = '0' + m;
    }
    h = '-' + h;
  }
  return {
    hour: h,
    min: m,
    sec: s,
  };
}

module.exports = {
  format: format,
  parse: parse
};

